import React from 'react'
import "./css/login.css"
import logo from './images/logo.png'
import { Form, Input, Button } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { connect } from 'react-redux'
import { reqLogin } from '../../api'
import { createDemo1Action, createDemo2Action } from '../../redux/actions/test'



function Login(props) {

  


  function onFinish(event) {
    // const { usernames, password } = values;
    // // console.log(values);
    // reqLogin(usernames, password).then((response) => {
    //   console.log(response);
    // }).catch((err) => {
    //   console.log(err);
    // })

    // 阻止事件的默认行为
    event.preventDefault()

    // 对所有表单字段进行检验
    this.props.form.validateFields(async (err, values) => {
      // 检验成功
      if (!err) {
        // console.log('提交登陆的ajax请求', values)
        // 请求登陆
        const { username, password } = values
        const result = await reqLogin(username, password) // {status: 0, data: user}  {status: 1, msg: 'xxx'}
        // console.log('请求成功', result)
        if (result.status === 0) { // 登陆成功
          // 提示登陆成功
          message.success('登陆成功')

          // 保存user
          const user = result.data
          memoryUtils.user = user // 保存在内存中
          storageUtils.saveUser(user) // 保存到local中

          // 跳转到管理界面 (不需要再回退回到登陆)
          this.props.history.replace('/')

        } else { // 登陆失败
          // 提示错误信息
          message.error(result.msg)
        }

      } else {
        console.log('检验失败!')
      }
    });

  }

  

  return (
    <div className="login">
      <header >
        <img src={logo} alt="logo" />
        <h1>商品管理系统</h1>
      </header>
      <section>
        <h1>用户登录</h1>
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{ remember: true }}
          onFinish={onFinish}
        >
          <Form.Item
            name="username"
            rules={[{ required: true, message: 'Please input your Username!' },
            { max: 12, message: '用户名必须小于12位' },
            { min: 4, message: '用户名必须大于4位' },
            { pattern: /^\w+$/, message: '用户名必须字母，数字，下划线组合' },]}
          >
            <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[
              { required: true, message: 'Please input your Password!' },
              // { max: 12, message: '用户名必须小于12位' },
              { min: 4, message: '用户名必须大于4位' },
              { pattern: /^\w+$/, message: '用户名必须字母，数字，下划线组合' }
            ]}
          >
            <Input
              prefix={<LockOutlined className="site-form-item-icon" />}
              type="password"
              placeholder="Password"
            />
          </Form.Item>
          <Form.Item>
            <Form.Item name="remember" valuePropName="checked" noStyle>

            </Form.Item>
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" className="login-form-button">
              Log in
            </Button>
          </Form.Item>
        </Form>
      </section>
    </div >

  )
}

export default connect(
  state => ({
    demo: state.testKey  //reducers中index里
  }),
  {
    demo1: createDemo1Action,  //actions里
    demo2: createDemo2Action,
  }
)(Login)
